import React, { Component } from 'react'
import {FaStar} from 'react-icons/fa'
import '../ratings.css'

export class Ratings extends Component {

    state = {
        loading: true,
        ratings: []
    };

    async componentDidMount(){
        const url = "http://substyleprod.com/projects/APIS/ratings.json";
        const response = await fetch(url);
        const data = await response.json();

        this.setState({ratings: data, loading: false})

        let rateSum = 0;
        let numRatings = 0;
        const len = data.length;
        let rater = null;
        for (let i = 0; i < len; i++) {
            rater = data[i];
            rateSum = data[i].rating + rateSum;
            numRatings = numRatings + 1;     
        }




        const avgRate = rateSum / len;
        let avgRateRounded =  Math.round(avgRate);
        document.getElementById('avgNum').innerHTML = avgRateRounded;

        let rating5 = data.filter(data => data.rating === 5).length;
        let rating4 = data.filter(data => data.rating === 4).length;
        let rating3 = data.filter(data => data.rating === 3).length;
        let rating2 = data.filter(data => data.rating === 2).length;
        let rating1 = data.filter(data => data.rating === 1).length;

        //console.log(rating5);
        document.getElementById("demo").innerHTML = rating5;
        document.getElementById("demo2").innerHTML =  rating4;
        document.getElementById("demo3").innerHTML = rating3;
        document.getElementById("demo4").innerHTML = rating2;
        document.getElementById("demo5").innerHTML = rating1;
        //console.log(rateSum)
        //console.log(avgRateRounded)


        //star rating average counter given from loop above
        if (avgRateRounded === 1) {
            document.getElementById("ping").className = "bar_1";
        } else if (avgRateRounded === 2) {
            document.getElementById("ping").className = "bar_2";
        } else if (avgRateRounded === 3) {
            document.getElementById("ping").className = "bar_3";
        } else if (avgRateRounded === 4) {
            document.getElementById("ping").className = "bar_4";
        }  else if (avgRateRounded === 5) {
           document.getElementById("ping").className = "bar_5";
        }  

    }

    render() {
        if (this.state.loading) {
            return <div className="textBox">...loading reviews...please wait....</div>
        }

        if(!this.state.ratings.length) {
            return <div className="textBox">No one's commenting! Be the first!</div>
        }

        return (

            <div className="textBox">

            <a name="ratings"></a>
            <hr/>
                <h3>Ratings</h3>

                <span className="heading">User Rating</span>

                <h5><span id="ping" className="bar_1"></span></h5>
                <p><span id="avgNum"></span> star average based on {this.state.ratings.length}  reviews.</p>

                <div className="row">
                <div className="side">
                    <div>5 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar" style="width=0%"></div>
                    </div>
                </div>
                <div className="side right">
                    <div id="demo"></div>
                </div>



                <div className="side">
                    <div>4 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-4"></div>
                    </div>
                </div>
                <div className="side right">
                    <div id="demo2"></div>
                </div>
                <div className="side">
                    <div>3 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-3"></div>
                    </div>
                </div>
                <div className="side right">
                    <div id="demo3"></div>
                </div>
                <div className="side">
                    <div>2 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-2"></div>
                    </div>
                </div>
                <div className="side right">
                    <div id="demo4"></div>
                </div>
                <div className="side">
                    <div>1 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-1"></div>
                    </div>
                </div>
                <div className="side right">
                    <div id="demo5"></div>
                </div>
            </div>
            <hr/>

                <p>{this.state.ratings.length} written reviews</p>


            <hr/>

            <>
            {this.state.ratings.map(rating => (
            <div className="textBox" key={rating.id}>

            <h5><span className={`bar_${rating.rating}`}></span></h5>


            <h6>{rating.header}</h6>
            <p>{rating.comment}</p>
            <p>{rating.name} - {rating.date}</p>
            <hr />
            </div>

            ))}
            </>
            </div>
        )
    }
}

export default Ratings
