import React, { Component } from 'react'
import {FaStar} from 'react-icons/fa'
import '../ratings.css'

export class Ratings extends Component {

    state = {
        loading: true,
        ratings: []
    };

    async componentDidMount(){
        const url = "http://substyleprod.com/projects/APIS/ratings.json";
        const response = await fetch(url);
        const data = await response.json();
        //const ratings = JSON.stringify(data);
        //const ratings = data;
        //console.log(ratings[1].rating)
        //console.log(ratings[2].rating)
        //console.log(ratings[3].rating)

        this.setState({ratings: data, loading: false})

        let rateSum = 0;
        let numRatings = 0;
        const len = data.length;
        let rater = null;

        for (let i = 0; i < len; i++) {
            rater = data[i].rating;
            if (rater.found) {
                rateSum = data.rating + rateSum;
                numRatings = numRatings + 1;
            }
        }
        const avgRate = rateSum / numRatings;
        console.log("Average rate:", rater);
        //console.log(Math.round(data[3].rating + data[2].rating / 35))






    }



    render() {

        if (this.state.loading) {
            return <div className="textBox">...loading reviews...please wait....</div>
        }

        if(!this.state.ratings.length) {
            return <div className="textBox">No one's commenting! Be the first!</div>
        }

        return (

            <div className="textBox">

            <a name="ratings"></a>
            <hr/>
                <h3>Ratings</h3>

                <span className="heading">User Rating</span>
                <span className="fa fa-star checked"></span>
                <span className="fa fa-star checked"></span>
                <span className="fa fa-star checked"></span>
                <span className="fa fa-star checked"></span>
                <span className="fa fa-star"></span>
                <p>4.1 average based on {this.state.ratings.length}  reviews.</p>

                <div className="row">
                <div className="side">
                    <div>5 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-5"></div>
                    </div>
                </div>
                <div className="side right">
                    <div>150</div>
                </div>
                <div className="side">
                    <div>4 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-4"></div>
                    </div>
                </div>
                <div className="side right">
                    <div>63</div>
                </div>
                <div className="side">
                    <div>3 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-3"></div>
                    </div>
                </div>
                <div className="side right">
                    <div>15</div>
                </div>
                <div className="side">
                    <div>2 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-2"></div>
                    </div>
                </div>
                <div className="side right">
                    <div>6</div>
                </div>
                <div className="side">
                    <div>1 star</div>
                </div>
                <div className="middle">
                    <div className="bar-container">
                    <div className="bar-1"></div>
                    </div>
                </div>
                <div className="side right">
                    <div>20</div>
                </div>
            </div>
            <hr/>

                <p>{this.state.ratings.length} written reviews</p>


            <hr/>

            <>
            {this.state.ratings.map(rating => (
            <div className="textBox" key={rating.id}>

            <h5><span className={`bar_${rating.rating}`}></span></h5>


            <h6>{rating.header}</h6>
            <p>{rating.comment}</p>
            <p>{rating.name} - {rating.date}</p>
            <hr />



            </div>




            ))}
            </>
            </div>
        )
    }
}

export default Ratings
